CREATE TABLE IF NOT EXISTS festival (
FNO INT AUTO_INCREMENT, ADDR1 VARCHAR(100), ADDR2 VARCHAR(100), BOOKTOUR INT, CAT1 VARCHAR(100), CAT2 VARCHAR(100), CAT3 VARCHAR(100), CONTENTID INT, CONTENTTYPEID INT, CREATEDTIME DATE, EVENTSTARTDATE DATE, EVENTENDDATE DATE, FIRSTIMAGE VARCHAR(100), FIRSTIMAGE2 VARCHAR(100), CPYRHTDIVCD VARCHAR(100), MAPX VARCHAR(100), MAPY VARCHAR(100), MLEVEL INT, MODIFIEDTIME DATE, AREACODE INT, SIGUNGUCODE INT, TEL VARCHAR(100), TITLE VARCHAR(100),
PRIMARY KEY (FNO) );
